import React,{Component} from "react";

 class Counter extends Component{ 

    // state={
    //     num: this.props.initialNumber
    // }

    constructor(props){
        super(props)
        this.state={
            num: props.initialNumber
        }
    }

    render(){
        return(
            <div>
                <h2>Counter</h2>
                <p>Initial value: {this.state.num}</p>
            </div>
        )
    }
}

export default Counter
